
public class Stringreverse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  String abc= "rahul";
		 String abc1="";
		for (int i=abc.length()-1;i>=0;i--)
		{
			
			abc1 = abc1+ abc.charAt(i);
			
		}
		System.out.println(abc1);
		
		for(int i=0;i<=10;i++)
		{
			System.out.println(i);
		}}

	
	
}
